defmodule Day1 do
  def parse_parens(string) do
    {count, neg_location} = parse_parens(string, 0, 0)
    {count, -1 * neg_location}
  end

  defp parse_parens("(" <> tail, count, pos) do
    pos =
      if pos >= 0 do
        pos + 1
      else
        pos
      end

    parse_parens(tail, count + 1, pos)
  end

  defp parse_parens(")" <> tail, count, pos) do
    pos =
      cond do
        pos < 0 -> pos
        count == -1 -> pos * -1
        true -> pos + 1
      end

    parse_parens(tail, count - 1, pos)
  end

  defp parse_parens("", count, pos) do
    {count, pos}
  end
end

